package pkg1;

public class p12 {

	public static int add(int a, int b) {
		return a+b;
	}
	
	public static int multiply (int a, int b) {
		return a*b;
	}
	
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int x = add(13, 20);
		int y = multiply(3, 9);
		System.out.println("Sum = " + x);
		System.out.println("Product = " + y);
	}

}
